import React from 'react'
import '../styles/tour-details.css'
import {Container, Row, Col, Form, ListGroup} from 'reactstrap'
import {useParams} from 'react-router-dom'
import tourData from '../assets/data/tours'
import calculateAvgRating from '../utils/avgRating'


const TourDetails = () => {
  
const {id} = useParams()

// lo usaremos con la API 
const tour = tourData.find(tour => tour.id ===id )

// D

const {photo,title, desc, price, address, reviews, city, distance, maxGroupSize} =  tour

const {totalRating, avgRating} =  calculateAvgRating (reviews)

return (
    <>
    <section>
      <Container>
        <Row>
          <Col lg="8">
            <div className="tour_content">
              <img src={photo} alt="" />
              </div>
              </Col>
              <div className="tour__info">
                <h2>{title}</h2>

                <div className="d-flex align-items-center gap-1">
                <span className='tour__location d-flex align-items-center gap-3'>
                <i class="ri-map-pin-user-fill"></i> {city}
                </span>

                <span className='tour__rating d-flex align-items-center gap-5'>
                <i class="ri-star-fill" style={{color: "var(--secondary-color)"}}></i> {calculateAvgRating === 0 ? null :avgRating }
                {totalRating === 0 ? 'Not rated' : <span>({reviews?.length})</span>} 
                </span>

              <span>
              <i class="ri-map-pin-user-fill"></i> {address}
              </span>
                </div>
                <div className="tour__extra-details">
                  <span><i class="ri-map-pin-2-line"></i> {city} </span>
                  <span><i class="ri-money-dollar-circle-fill"></i> ${price} Por persona</span>
                  <span><i class="ri-group-fill"></i> {maxGroupSize} </span>
                </div>
              </div>
            
          
        </Row>
      </Container>
    </section>
    
    
    
    </>
);
};

export default TourDetails
